//import RightButton from './RightButton';

import React from 'react';
import '../styles/Slider.css';

class Slider extends React.Component {
	constructor(props) {
		super(props);

		const array = this.props.array;

		this.view = this.getImages(array);
		//this.state = {index, }
	}

	//function for rendering images
	getImages(array) {
		let images = array.map(image => {
			return <img className="imageList" src={require(`../assets/${image}.jpg`)} />;
		});
		return(
			this.renderSlides(images)
		);
	}

	renderSlides(images){
		return(
			<div>
				{images.filter(i => i === images[images.length-1])}
			</div>
		)
	}

	//handleScroll
		//onScroll handler

	//handleClick
		//enlarge photo

	render() {
		return (
			<div>{this.view}</div>
		);
	}
}

export default Slider
