//import LeftButton from './LeftButton';
//import RightButton from './RightButton';

import React, { Component } from 'react';
// import './Slider.css';

class Slider extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			view: this.renderSlides(this.props.array)
		}
	}

	//function for creating three slides
	renderSlides(props){
		let array = this.props.array
		let images = array.map(image => {
			return <img src={require(`../../assets/${image}.jpg`)} />
		});
		return(<ul>{images}</ul>);
	}

	render() {
		return (
			<div>
				<div className="slider">
				{this.state.view}
				</div>
			</div>
		);
	}
}

export default Slider;
