import '../styles/Slider.css';

class Slider extends React.Component {
	constructor(props) {
		super(props);
		this.view = this.getImages(this.props.array)
		//this.state = {index, }
	}

	//function for rendering images
	getImages(props) {
		let images = this.props.array.map(image => {
			return <img className="imageList" src={require(`../../assets/${image}.jpg`)} />
		});

		return(
			this.renderShow(images)
		);
	}

	renderShow(images) {
		return (
			<ul> {images[0]} </ul>
		);	
	}

